mapper.select:
SELECT c_id, c_pwd, c_email, c_name, c_phone,
TO_CHAR(c_regdate,
'yyyy-mm-dd') c_regdate, c_disabled
FROM
( SELECT list.*, ROWNUM AS RNUM
FROM (

ORDER BY c_regdate desc
) list
)



			RNUM BETWEEN #{start_row} AND #{end_row}

mapper.update:
UPDATE client_tbl
SET
c_disabled = #{c_disabled}
WHERE
c_id = #{c_id}
