		FROM (

		ORDER BY q_num desc
		) list
	)

SELECT q_num, c_id, q_title, q_content, q_file
FROM question_tbl
WHERE q_num = #{q_num}

SELECT NVL((
	SELECT 1
	FROM question_tbl
	WHERE q_num = #{q_num} AND c_id = #{c_id}
), 0) as state
FROM DUAL
