mapper.select:
SELECT q_num, q_title, c_id, a_id, to_char(q_regdate,'YYYY-MM-DD HH24:MI') as q_regdate, q_state, q_ref, q_step from (select list.*, rownum as rnum from(
order by q_ref desc, q_step asc ) list )
rnum between #{start_row} and #{end_row}

select nvl(count(1), 0) from(
select list.*, rownum as rnum
from(

order by q_num desc
) list
)
